mapper.select:
select distinct rm.menu_id from sys_user_role ur 
LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id
where ur.user_id = #{userId}

select u.email,u.mobile,u.create_time createTime,u.user_id userId,u.status,u.username,c.name college, c.coid cValue, s.name shool,s.scid sValue,p.pname province,p.provinceID pValue from sys_user u left join tb_school s on s.scid = u.school left join tb_college c on c.coid = s.collegeID left join tb_region_province p on p.provinceID = c.provinceID
u.username = #{page.username}
LIMIT #{page.page},#{page.limit}
